import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import validator from 'validator'
import { func } from "prop-types";
import { validate } from "json-schema";


const SignIn = () => {
    const[email,setEmail]=useState("");
    const[password,setPassword]=useState("");
    const[validateEmail,setValidateEmail]=useState(true)
    const[validatePassword,setValidatePassword]=useState(true)
    function validEmail(){
      if(validator.isEmail(email)){
        setValidateEmail(true)
      }else{
        setValidateEmail(false)
      }
    }
    return ( <div>
         <div className="container">
        <div className="d-flex justify-content-center align-self-center mt-5">
            <form>
                <h2 className="text-center mb-4">Login</h2>
                <div className="form-outline mb-4">
                  <label className="form-label" for="form2Example1">Email address</label>
                  <input  required value={email}  onBlur={validEmail} onChange={e=>setEmail(e.target.value)} type="email" id="form2Example1" className="form-control"></input>
                  {validateEmail && <div className="invalid-feedback">
                    Invalid Email
                  </div>}
                  <div className="invalid-feedback">
                    Invalid Email
                  </div>
                </div>
              
                <div className="form-outline mb-4">
                  <label className="form-label" for="form2Example2">Password</label>
                  <input type="password" id="form2Example2" className="form-control is-invalid"></input>
                  <div className="invalid-feedback">
                    Invalid good!
                  </div>
                </div>
                <button type="button" className="btn btn-primary btn-block mb-4 w-100">Sign in</button>
              
                <div className="text-center">
                  <p>Not a member? <a href="#!">Register</a></p>
                </div>
              </form>
        </div>
    </div>
    </div>);
}
 
export default SignIn;
